    public void refresh() {

        quantCadastros.setText(String.valueOf(CadastroVO.getTotal()));
        quantMesasOcupadas.setText(String.valueOf(RestauranteBO.quantidadeMesasOcupada()));
        quantFila.setText(String.valueOf(ListaEsperaVO.getFila().size()));
    }

    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed

        if (buttonDesocupar.isSelected()) {

            int mesa = Integer.parseInt(escoMesa.getText());

            if (mesa < 0 || mesa > 5) {
                JOptionPane.showMessageDialog(null, "Mesa inexistente.", null, JOptionPane.INFORMATION_MESSAGE);

            } else if (RestauranteVO.getListaMesas().get(mesa).getCliente() == null) {
                JOptionPane.showMessageDialog(null, "Mesa vazia.", null, JOptionPane.INFORMATION_MESSAGE);

            } else {
                JOptionPane.showMessageDialog(null, "Mesa desocupada.", null, JOptionPane.INFORMATION_MESSAGE);
                RestauranteBO.desocupar(mesa);
                refresh();
            }
        }

        if (buttonDados.isSelected()) {

            int mesa = Integer.parseInt(escoMesa.getText());

            if (mesa < 0 || mesa > 5) {
                JOptionPane.showMessageDialog(null, "Mesa inexistente.", null, JOptionPane.INFORMATION_MESSAGE);

            } else if (RestauranteVO.getListaMesas().get(mesa).getCliente() == null) {
                JOptionPane.showMessageDialog(null, "Mesa vazia.", null, JOptionPane.INFORMATION_MESSAGE);

            } else {

                String nome = RestauranteVO.getListaMesas().get(mesa).getCliente().getNome();
                int cadeiras = RestauranteVO.getListaMesas().get(mesa).getCadeiras();

                JOptionPane.showMessageDialog(null, "Nome cliente: " + nome + "\n"
                        + "Cadeiras: " + cadeiras, null, JOptionPane.INFORMATION_MESSAGE);

            }
        }


    }//GEN-LAST:event_jButton1ActionPerformed

    private void buscarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_buscarActionPerformed

        String nome = textFila.getText();

        if (textFila == null) {
            JOptionPane.showMessageDialog(null, "Digite um nome.", null, JOptionPane.INFORMATION_MESSAGE);

        } else {

            ListaEsperaBO.buscarIndice(nome);

        }


    }//GEN-LAST:event_buscarActionPerformed

    private void buttonAlocarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_buttonAlocarActionPerformed
        ListaEsperaBO.autoAlocarCliente();
        refresh();
    }//GEN-LAST:event_buttonAlocarActionPerformed

